

#Domain regexp https://regexr.com/3au3g
REGEXP_DOMAIN = "(?:[a-z0-9](?:[a-z0-9-]{0,61}[a-z0-9])?\.)+[a-z0-9][a-z0-9-]{0,61}[a-z0-9]"




R1 = r"(?:\s|^|\/|\\|\[|\,|\|)" # Перед доменом либо пробел, таб, или начало строки
R2 = "(?:[a-z0-9](?:[a-z0-9-]{0,61}[a-z0-9])?\.)+" # любое количество чегото начинающегося на букуву или цифру, потом может быть дефис и в конце буква или цифра  и точка в конце    333.eefef33.eefef.wefwef.wfe.33fewf.
